import{_ as i,c as t,o,a2 as c}from"./chunks/framework.CnKoBJ9A.js";const h=JSON.parse('{"title":"Git 常用操作","description":"","frontmatter":{},"headers":[],"relativePath":"articles/Git/Git常用操作.md","filePath":"articles/Git/Git常用操作.md"}'),l={name:"articles/Git/Git常用操作.md"};function a(p,e,d,r,n,g){return o(),t("div",null,e[0]||(e[0]=[c('<h1 id="git-常用操作" tabindex="-1">Git 常用操作 <a class="header-anchor" href="#git-常用操作" aria-label="Permalink to &quot;Git 常用操作&quot;">​</a></h1><ul><li><p>克隆仓库：<code>git clone https://github.com/xxx.git</code></p></li><li><p>创建分支：<code>git branch [name]</code></p></li><li><p>切换分支：<code>git checkout [name]</code></p></li><li><p>检出分支：<code>git checkout -b [name]</code></p></li><li><p>新增远程分支：<code>git push --set-upstream origin xxx</code></p></li><li><p>抓取远程分支：<code>git fetch origin [name]</code></p></li><li><p>同步远程分支：<code>git pull origin [name]</code></p></li><li><p>删除本地分支：<code>git branch -d [name]</code></p></li><li><p>删除远程分支：<code>git push origin --delete [name]</code></p></li><li><p>查看远程仓库地址：<code>git remote -v</code></p></li><li><p>添加远程仓库：<code>git remote add origin https://github.com/xxx/xxx.git</code></p></li><li><p>抓取远程仓库：<code>git fetch [remote-name]</code></p></li><li><p>同步远程仓库：<code>git pull [remote-name]</code></p></li><li><p>查看标签：<code>git tag</code></p></li><li><p>添加用户：<code>git config --global user.name</code></p></li><li><p>注销用户：<code>git config --global --unset user.name</code></p></li><li><p>初始化本地库：<code>git init</code></p></li><li><p>发布新建的本地分支到远程分支：<code>git push -u origin xxx</code></p></li><li><p>更新远程分支列表：<code>git remote update origin --prune</code></p></li><li><p>放弃已追踪的某个文件：<code>git update-index --assume-unchanged filename</code></p></li><li><p>撤销放弃已追踪的某个文件：<code>git update-index --no-assume-unchanged filename</code></p></li><li><p>关闭大小写不敏感：<code>git config core.ignorecase false</code></p></li><li><p>放弃已被追踪的某个目录：<code>git rm -r --cached docs/.vitepress/cache</code></p></li><li><p><a href="https://git-scm.com/book/zh/v2" target="_blank" rel="noreferrer">Git 官方文档</a></p></li><li><p><a href="https://git-scm.com/book/zh/v2/%E8%87%AA%E5%AE%9A%E4%B9%89-Git-Git-%E9%92%A9%E5%AD%90" target="_blank" rel="noreferrer">工作流钩子</a></p></li><li><p><a href="https://typicode.github.io/husky/" target="_blank" rel="noreferrer">husky</a></p></li></ul><h2 id="默认配置文件位置" tabindex="-1">默认配置文件位置 <a class="header-anchor" href="#默认配置文件位置" aria-label="Permalink to &quot;默认配置文件位置&quot;">​</a></h2><p>macos 系统中：<code>~/.gitconfig</code></p>',4)]))}const m=i(l,[["render",a]]);export{h as __pageData,m as default};
