import{_ as d,c as e,o as r,a2 as a}from"./chunks/framework.CnKoBJ9A.js";const i="/assets/ipd.CDItz_DG.jpeg",P=JSON.parse('{"title":"IPD","description":"","frontmatter":{},"headers":[],"relativePath":"intelligent-car/IPD.md","filePath":"intelligent-car/IPD.md"}'),n={name:"intelligent-car/IPD.md"};function o(l,t,c,s,T,R){return r(),e("div",null,t[0]||(t[0]=[a('<h1 id="ipd" tabindex="-1">IPD <a class="header-anchor" href="#ipd" aria-label="Permalink to &quot;IPD&quot;">​</a></h1><p>集成产品开发（Integrated Product Development, 简称 IPD）是一套产品开发的模式、理念与方法。IPD 的思想来源于美国 PRTM 公司出版的《产品及生命周期优化法》（简称 PACE——Product And Cycle-time Excellence）一书，该书中详细描述了这种新的产品开发模式所包含的各个方面。</p><p><img src="'+i+'" alt="IPD"></p><h2 id="ipd-相关缩写" tabindex="-1">IPD 相关缩写 <a class="header-anchor" href="#ipd-相关缩写" aria-label="Permalink to &quot;IPD 相关缩写&quot;">​</a></h2><table tabindex="0"><thead><tr><th>英文缩写</th><th>英文全称</th><th>中文释义</th></tr></thead><tbody><tr><td>AR</td><td>Allocation Requirement</td><td>团队级需求-任务</td></tr><tr><td>BBFV</td><td>building block function verification</td><td>构建模块功能验证</td></tr><tr><td>CBB</td><td>Common Building Block</td><td>公用构建模块</td></tr><tr><td>DC</td><td>Direct Current</td><td>直流电源工程师</td></tr><tr><td>EC</td><td>Engineering Change</td><td>工程变更</td></tr><tr><td>ECR</td><td>Engineering Change Request</td><td>工程变更申请</td></tr><tr><td>EE</td><td>Electronic Engineer</td><td>电子工程师</td></tr><tr><td>EMC</td><td>Electro Magnetic Capability</td><td>电磁兼容</td></tr><tr><td>EOL</td><td>End Of Life</td><td>生命周期介绍</td></tr><tr><td>FAQ</td><td>Frequently Asked Questions</td><td>常见问题</td></tr><tr><td>IR</td><td>Initial Requiremen</td><td>初始来自客户的需求</td></tr><tr><td>ME</td><td>Mechanical Engineer</td><td>机构工程师</td></tr><tr><td>PCR</td><td>Plan Change Request</td><td>计划变更申请（请求）</td></tr><tr><td>PDT</td><td>Product Development Team</td><td>产品开发团队</td></tr><tr><td>PLM</td><td>Produce Life-cycle Management</td><td>产品生命周期管理</td></tr><tr><td>QA</td><td>Quality Assurance</td><td>质量保证体系</td></tr><tr><td>SDV</td><td>system design verification</td><td>系统设计验证</td></tr><tr><td>SIT</td><td>system integration testing</td><td>系统集成测试</td></tr><tr><td>SOP</td><td>Standard Operation Procedure</td><td>标准作业程序</td></tr><tr><td>SR</td><td>System Requirement</td><td>系统需求</td></tr><tr><td>SVT</td><td>system verification testing</td><td>系统验证测试</td></tr><tr><td>SWE</td><td>Software Engineer</td><td>软件工程师</td></tr><tr><td>TR</td><td>Technical Review(Technology Review)</td><td>技术评审</td></tr><tr><td>TR1</td><td>TR</td><td>概念阶段技术评审点：产品需求和概念技术评审（业务需求评审）</td></tr><tr><td>TR2</td><td>TR2</td><td>计划阶段技术评审点 1：需求分解和需求规格评审（功能需求评审，产品级规格）</td></tr><tr><td>TR3</td><td>TR3</td><td>计划阶段技术评审点 2：总体方案评审（系统设计，架构设计，概要设计）</td></tr><tr><td>TR4</td><td>TR4</td><td>开发阶段技术评审点 1：模块/系统评审（详细设计，BBFV 测试结果）</td></tr><tr><td>TR4A</td><td>TR4A</td><td>开发阶段技术评审点 2：原形机的质量 SDV 结果和初始产品的准备情况</td></tr><tr><td>TR5</td><td>TR5</td><td>开发阶段技术评审点 3：初始产品的质量（SIT 结果）（SIT Alpha 测试技术评审）</td></tr><tr><td>TR6</td><td>TR6</td><td>验证阶段技术评审点：发布评审（SVT Beta 测试、制造系统验证等）</td></tr><tr><td>TRx</td><td>TRx</td><td>TR1：产品需求和概念评审, TR2：需求分解和规格评审, TR3：总体方案评审, TR4：模块/系统评审, TR4A： 集成测试评审, TR5：样机评审, TR6：小批量评审</td></tr></tbody></table><h2 id="参考资料" tabindex="-1">参考资料 <a class="header-anchor" href="#参考资料" aria-label="Permalink to &quot;参考资料&quot;">​</a></h2><ul><li><a href="https://baike.baidu.com/item/IPD/3072209" target="_blank" rel="noreferrer">IPD</a></li></ul>',7)]))}const u=d(n,[["render",o]]);export{P as __pageData,u as default};
